    }

    memset(buf, 0, MAX_LEN);

    vfs_read(f, buf, MAX_LEN, &f->f_pos);

    ver = strsep(&buf, " ");
    ver = strsep(&buf, " ");
    ver = strsep(&buf, " ");

    filp_close(f, 0);   
